<?php

declare(strict_types = 1);

namespace CodeTool\ElasticSearch\DSL\Aggregation\Metrics;

use CodeTool\ElasticSearch\DSL\Aggregation\ElasticSearchAggregationInterface;

/**
 * SumAggregation is a single-value metrics aggregation that sums up
 * numeric values that are extracted from the aggregated documents.
 * These values can be extracted either from specific numeric fields
 * in the documents, or be generated by a provided script.
 * @see: http://www.elasticsearch.org/guide/en/elasticsearch/reference/current/search-aggregations-metrics-sum-aggregation.html
 */
class ElasticSearchAggregationMetricsSum implements ElasticSearchAggregationInterface
{
    /**
     * @var string
     */
    private $field = '';

    // private $script;

    /**
     * @var string
     */
    private $format = '';

    /**
     * @var ElasticSearchAggregationInterface[]
     */
    private $subAggregations = [];

    /**
     * @var string[]
     */
    private $meta = [];

    public function field(string $field)
    {
        $this->field = $field;

        return $this;
    }

    public function format(string $format)
    {
        $this->format = $format;

        return $this;
    }

    public function subAggregation(string $name, ElasticSearchAggregationInterface $subAggregation)
    {
        $this->subAggregations[$name] = $subAggregation;

        return $this;
    }

    public function meta(array $metaData)
    {
        $this->meta = $metaData;

        return $this;
    }

    public function jsonSerialize()
    {
        $options = [];
        if ('' !== $this->field) {
            $options['field'] = $this->field;
        }

        if ('' !== $this->format) {
            $options['format'] = $this->format;
        }

        $result = ['sum' => $options];

        if (0 !== count($this->subAggregations)) {
            $result['aggregations'] = array_map(
                function (ElasticSearchAggregationInterface $searchAggregation) {
                    return $searchAggregation->jsonSerialize();
                },
                $this->subAggregations
            );
        }

        if (0 !== count($this->meta)) {
            $result['meta'] = $this->meta;
        }

        return $result;
    }
}
